package com.bgood.xn.ui.message;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.RadioGroup;import android.widget.RadioGroup.OnCheckedChangeListener;import com.bgood.xn.R;import com.bgood.xn.ui.BaseFragment;/** * @todo:聊天主界面 * @author:hg_liuzl@163.com */public class MessageFragment extends BaseFragment implements OnCheckedChangeListener {		public static final int MESSAGE_MAIN = 0;	public static final int MESSAGE_FRIEND = 1;	public static final int MESSAGE_GROUP = 2;	public static final int MESSAGE_COMMUNICATION_HALL = 3;		private int messageCheckType = 0;	private RadioGroup radioGroup;	private View b_message_main_add;		@Override	public View onCreateView(LayoutInflater inflater, ViewGroup container,Bundle savedInstanceState) {		layout = inflater.inflate(R.layout.layout_message_main, container, false);		initViews();		return layout;	}	/**	 * 初始化tabhost	 */	private void initViews()	{		radioGroup = (RadioGroup) layout.findViewById(R.id.rg_message_main_tab);		radioGroup = (RadioGroup) layout.findViewById(R.id.b_message_main_add);		radioGroup.setOnCheckedChangeListener(this);	}	@Override	public void onCheckedChanged(RadioGroup group, int checkedId)	{		switch (checkedId)		{		case R.id.message_main_tab_messagecenter:			messageCheckType = MESSAGE_MAIN;			b_message_main_add.setVisibility(View.INVISIBLE);			break;		case R.id.message_main_tab_friend:			messageCheckType = MESSAGE_FRIEND;			b_message_main_add.setVisibility(View.VISIBLE);			break;		case R.id.message_main_tab_group:			messageCheckType = MESSAGE_GROUP;			b_message_main_add.setVisibility(View.VISIBLE);			break;		case R.id.message_main_tab_communication_hall:			b_message_main_add.setVisibility(View.INVISIBLE);			messageCheckType = MESSAGE_COMMUNICATION_HALL;			break;		}	}}