package com.bgood.xn.ui.xuanneng;import android.content.Intent;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.View.OnClickListener;import android.view.ViewGroup;import com.bgood.xn.R;import com.bgood.xn.ui.BaseFragment;import com.bgood.xn.ui.xuanneng.joke.JokeMainActivity;/** *  * @todo:炫能模块主界面 * @date:2014-11-21 下午4:45:15 * @author:hg_liuzl@163.com */public class XuannengFragment extends BaseFragment {	public static final int XUANNENG_JOKE = 0;	private View layout;	@Override	public View onCreateView(LayoutInflater inflater, ViewGroup container,Bundle savedInstanceState) {		layout = inflater.inflate(R.layout.layout_xuanneng_main, container, false);		layout.findViewById(R.id.xuanneng_b_humor).setOnClickListener(new OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent(mActivity, JokeMainActivity.class);				startActivity(intent);			}		});		return layout;	}}